import './App.css'
import {useState} from 'react'

const cardImages = [
  {"src": "/img/Ludwig.png"},
  {"src": "/img/ClericBeast.png"},
  {"src": "/img/EileentheCrow.png"},
  {"src": "/img/OrphanofKos.png"},
  {"src": "/img/Gerhman.png"},
  {"src": "/img/BloodStarvedBeast.png"}
]

function App() {
  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)

  //shuffle cards 
  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({...card, id: Math.random() }))

      setCards(shuffledCards)
      setTurns(0)
  }

  console.log(cards, turns)

  return (
    <div className="App">
      <h1>Bloodborne Match</h1>
      <button onClick={shuffleCards}>New Game</button>

      <div className="card-grid">
        {cards.map(card => (
          <div className="card" key={card.id}> 
            <div>
              <img className="front" src={card.src} alt="card front" />
              <img className="back" src="/img/HuntersMark.png" alt="card front" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App
